sys_script_include.script:
var SIToGetDeptName =Class.create();
SIToGetDeptName.prototype = Object.extendsObject(global.AbstractAjaxProcessor,
{
	getDepartment :function()
	{
		var GR_Dept = new GlideRecord('sys_user');
		GR_Dept.addQuery('department',current.department);
		GR_Dept.query();
		var services=[];
		while(GR_Dept.next())
			{
			   services.push(GR_Dept.sys_id+'');
				gs.log('found: ' + GR_Dept.sys_id.toString());
			}
		return "sys_idIN" + services.join(",");
	},
	type:'SIToGetDeptName'
});
